(function($){$.caretTo=function(el,index){if(el.createTextRange){var range=el.createTextRange();range.move("character",index);range.select()}else if(el.selectionStart!=null){el.focus();el.setSelectionRange(index,index)}};$.fn.caretTo=function(index,offset){return this.queue(function(next){if(isNaN(index)){var i=$(this).val().indexOf(index);if(offset===!0){i+=index.length}else if(offset){i+=offset}
$.caretTo(this,i)}else{$.caretTo(this,index)}
next()})};$.fn.caretToStart=function(){return this.caretTo(0)};$.fn.caretToEnd=function(){return this.queue(function(next){$.caretTo(this,$(this).val().length);next()})}}(jQuery))
